mapper.delete:
delete from t_price where 

	 area_id = #{areaId}


	 area_id is null


	 and community_id = #{communityId}


	 and community_id is null

mapper.update:
update t_price set status = #{status} where 

	 area_id = #{areaId}


	 area_id is null


	 and community_id = #{communityId}


	 and community_id is null
