mapper.select:
select id,licence_code,status,create_time,start_time,end_time,limit_day,
	max_use_count,org_id,mac_card_info,cpu_info,harddisk_info,bind_time,update_time,ebp_auth,sdp_auth,am_auth,batch_number
 from OVERSEAS_EBP_SOFT_LICENCE
where 1=1
and id=#{id}
and org_id=#{orgId}
and licence_code like CONCAT('%',#{licence_code},'%')
and status=#{status}
and date_format(create_time,'yyyy-mm-dd')>=#{startTime}
and date_format(create_time,'yyyy-mm-dd')<=#{endTime}
and batch_number like CONCAT('%',#{batch_number},'%')


	and id in #{item}


order by create_time desc
