mapper.select:
SELECT substring(data_date,6,5) AS data_date, data_source, upload_time, bg_type, bg_value FROM hd_blood_glucose 
WHERE member_id = #{memberId,jdbcType=VARCHAR} 
	AND data_date >= #{startTime,jdbcType=VARCHAR}
	AND data_date <= #{endTime,jdbcType=VARCHAR} 
	ORDER BY hd_blood_glucose.data_date ASC, bg_type ASC,hd_blood_glucose.upload_time DESC
