select emp.org_id,sys.user_id, emp.emp_id, emp.real_name, emp.specialty, emp.phone_no, emp.identity_card, emp.sex, emp.email from employee emp,sys_user sys where 1=1
and emp.org_id in #{item}
and sys.user_code LIKE CONCAT('%',#{userCode},'%')
and emp.real_name LIKE CONCAT('%',#{realName},'%')
and emp.specialty = #{specialty,jdbcType=VARCHAR}
and emp.status = #{status} and sys.user_id = emp.user_id order by emp.update_time desc,emp_id desc

