mapper.update:
update t_url_blacklist_rule 
set 

	, name=#{name}
	, memo=#{memo}
	, update_time=#{updateTime}

where id=#{id}

mapper.select:
select t1.*,t2.user_name as creator_name ,(select count(*) from t_url_blacklist where url_blacklist_rule_id = t1.id) as urlTotal from t_url_blacklist_rule t1 left join ta_manage_user_info t2 on t1.creator=t2.id 
	where 1=1
	and t1.id=#{_parameter}
