mapper.update:
update t_order_school
set 

	, licence_code=#{licenceCode}
	, update_time=#{updateTime}

where id=#{id}

mapper.select:
	select t1.*,t2.name as school_name,
(select sum(quan) from t_order_school_terminal_device where order_school_id=t1.id) as device_count,
(select count(*) from t_device_info where school_id=t1.school_id and device_status=2 and order_id = #{orderId}) as device_use_count
from t_order_school t1 left join t_school t2 on t1.school_id = t2.id
		where 1=1
		and t1.id=#{id}
		and t1.order_id=#{orderId}
		and t1.school_id=#{schoolId}
		and t1.licence_code=#{licenceCode}
		order by t1.create_time desc

		select t1.*,t2.name as school_name
from t_order_school t1 left join t_school t2 on t1.school_id = t2.id
		where 1=1
		and t1.id=#{id}
		and t1.order_id=#{orderId}
		and t1.school_id=#{schoolId}
		and t1.licence_code=#{licenceCode}
		order by t1.create_time desc
